t = [0:0.01:0.98];y1 = sin(2*pi*4*t);% plotplot(t, y1)     % horizontal axis = t, vertical axis = y1y2 = cos(2*pi*4*t);% plot on top of each otherhold on;plot(t, y2, 'r')xlabel('Time')ylabel('Value')legend('sine', 'cosine')title('PLOT')% save plotprint -dpng 'myplot.png'% get rid of plotclose% shortcut to save figurefigure(1); plot(t, y1)figure(2); plot(t, y2)% multiple plots subplot(1, 2, 1);     % Divide plots into 1 x 2 grid, and work on first element (1)plot(t, y1)subplot(1, 2, 2);plot(t, y2)% set scale on the subplotaxis([0.5 1 -1 1])    % x-axis from 0.5 to 1 && y-axis from -1 to 1% clear figureclf% plot color mapA = magic(10)imagesc(A)imagesc(A), colorbar, colormap gray;